package day5_IntroToJavaOperators;

import java.util.Scanner;

public class ModifyX {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
        int x = sc.nextInt();
        x *= 5;
        x += 10;
        x /= 2;

        System.out.println(x);

	}

}
